import React, { useState, useEffect } from 'react';
import Calendar from 'react-calendar';
import 'react-calendar/dist/Calendar.css';
import { fetchBookings } from '../services/api';
import {
  Container,
  Typography,
  List,
  ListItem,
  ListItemText,
  Box,
} from '@mui/material';

const CalendarPage = () => {
  const [selectedDate, setSelectedDate] = useState(new Date());
  const [bookings, setBookings] = useState([]);

  // Загрузка данных при монтировании компонента
  useEffect(() => {
    const loadBookings = async () => {
      try {
        const data = await fetchBookings();
        setBookings(data);
      } catch (error) {
        console.error('Ошибка загрузки встреч:', error);
      }
    };
    loadBookings();
  }, []);

  // Фильтрация встреч по выбранной дате
  const filteredBookings = bookings.filter(
    (booking) =>
      new Date(booking.start).toDateString() === selectedDate.toDateString()
  );

  return (
    <Container sx={{ marginTop: 8 }}>
      <Typography variant="h4" gutterBottom align="center">
        Календарь встреч
      </Typography>

      <Box sx={{ display: 'flex', flexDirection: 'column', alignItems: 'center' }}>
        <Calendar
          onChange={setSelectedDate}
          value={selectedDate}
        />

        <Typography variant="h6" sx={{ marginTop: 4 }}>
          Встречи на {selectedDate.toLocaleDateString()}:
        </Typography>

        {filteredBookings.length === 0 ? (
          <Typography color="text.secondary">Нет запланированных встреч</Typography>
        ) : (
          <List>
            {filteredBookings.map((booking) => (
              <ListItem key={booking.id}>
                <ListItemText
                  primary={booking.title}
                  secondary={`${new Date(
                    booking.start
                  ).toLocaleTimeString()} - ${new Date(
                    booking.end
                  ).toLocaleTimeString()}`}
                />
              </ListItem>
            ))}
          </List>
        )}
      </Box>
    </Container>
  );
};

export default CalendarPage;
