import React from 'react';
import { AppBar, Toolbar, Typography, Button, Box } from '@mui/material';
import { Link, useNavigate } from 'react-router-dom';
import useStores from '../hooks/useStores';

const NavBar = () => {
  const navigate = useNavigate();
  const { userStore } = useStores();

  // Обработка выхода
  const handleLogout = () => {
    userStore.logout(); // Очистка состояния пользователя
    navigate('/'); // Перенаправление на страницу входа
  };

  return (
    <AppBar
      position="static"
      sx={{
        background: '#00796b',
        padding: 2,
        boxShadow: 'none', // Убираем тень
        borderRadius: 0,   // Убираем закругления
      }}
    >
      <Toolbar
        sx={{
          display: 'flex',
          justifyContent: 'center',
          width: '100%',
          maxWidth: 1200,
        }}
      >
        <Typography
          variant="h6"
          component="div"
          sx={{ fontWeight: 'bold', mr: 5 }}
        >
          Панель управления
        </Typography>

        <Box
          sx={{
            display: 'flex',
            justifyContent: 'center',
            gap: 3,
          }}
        >
          <Button color="inherit" component={Link} to="/dashboard">
            Главная
          </Button>
          <Button color="inherit" component={Link} to="/schedule">
            Расписание
          </Button>
          <Button color="inherit" component={Link} to="/bookings/new">
            Создать встречу
          </Button>
          <Button color="inherit" component={Link} to="/calendar">
            Календарь
          </Button>
          <Button color="inherit" onClick={handleLogout}>
            Выйти
          </Button>
        </Box>
      </Toolbar>
    </AppBar>
  );
};

export default NavBar;
