import React, { useState } from 'react';
import { observer } from 'mobx-react-lite';
import useStores from '../hooks/useStores';
import { createBooking } from '../services/api';
import {
  Container,
  Typography,
  TextField,
  Button,
  Box,
  Paper,
} from '@mui/material';

const BookingPage = observer(() => {
  const [title, setTitle] = useState('');
  const [startDate, setStartDate] = useState('');
  const [startTime, setStartTime] = useState('');
  const [endDate, setEndDate] = useState('');
  const [endTime, setEndTime] = useState('');
  const { scheduleStore } = useStores();

  const handleCreate = async () => {
    if (!title || !startDate || !startTime || !endDate || !endTime) {
      alert('Заполните все поля!');
      return;
    }

    const start = new Date(`${startDate}T${startTime}`).toISOString();
    const end = new Date(`${endDate}T${endTime}`).toISOString();

    if (new Date(start) >= new Date(end)) {
      alert('Дата начала должна быть раньше даты окончания!');
      return;
    }

    try {
      const newBooking = await createBooking({
        title,
        start,
        end,
      });

      scheduleStore.addBooking(newBooking);

      alert(`Встреча успешно создана!`);

      // Очистка полей
      setTitle('');
      setStartDate('');
      setStartTime('');
      setEndDate('');
      setEndTime('');
    } catch (error) {
      console.error('Ошибка при создании встречи:', error);
      alert('Ошибка при создании встречи!');
    }
  };

  return (
    <Container maxWidth="sm">
      <Paper elevation={3} sx={{ padding: 4, marginTop: 8 }}>
        <Typography variant="h4" gutterBottom align="center">
          Создание встречи
        </Typography>

        <Box
          component="form"
          sx={{
            display: 'flex',
            flexDirection: 'column',
            gap: 2,
          }}
        >
          <Typography variant="h6">Название встречи</Typography>
          <TextField
            variant="outlined"
            value={title}
            onChange={(e) => setTitle(e.target.value)}
            required
          />

          <Typography variant="h6">Дата начала встречи</Typography>
          <TextField
            type="date"
            variant="outlined"
            value={startDate}
            onChange={(e) => setStartDate(e.target.value)}
            required
          />

          <Typography variant="h6">Время начала</Typography>
          <TextField
            type="time"
            variant="outlined"
            value={startTime}
            onChange={(e) => setStartTime(e.target.value)}
            required
          />

          <Typography variant="h6">Дата окончания встречи</Typography>
          <TextField
            type="date"
            variant="outlined"
            value={endDate}
            onChange={(e) => setEndDate(e.target.value)}
            required
          />

          <Typography variant="h6">Время окончания</Typography>
          <TextField
            type="time"
            variant="outlined"
            value={endTime}
            onChange={(e) => setEndTime(e.target.value)}
            required
          />

          <Button
            variant="contained"
            color="primary"
            onClick={handleCreate}
            size="large"
          >
            Создать встречу
          </Button>
        </Box>
      </Paper>
    </Container>
  );
});

export default BookingPage;
