import React from 'react';
import { useNavigate } from 'react-router-dom';
import { observer } from 'mobx-react-lite';
import useStores from '../hooks/useStores';
import {
  Container,
  Typography,
  Grid,
  Card,
  CardContent,
  CardActions,
  Button,
  Box,
} from '@mui/material';

const DashboardPage = observer(() => {
  const { userStore } = useStores();
  const navigate = useNavigate();

  // Обработка выхода
  const handleLogout = () => {
    userStore.logout(); // Очистка состояния пользователя
    navigate('/'); // Перенаправление на страницу входа
  };

  const sections = [
    {
      title: 'Расписание',
      path: '/schedule',
      description: 'Просмотр и управление встречами',
    },
    {
      title: 'Создать встречу',
      path: '/bookings/new',
      description: 'Создание новой встречи',
    },
    {
      title: 'Календарь',
      path: '/calendar',
      description: 'Просмотр встреч в календаре',
    },
  ];

  return (
    <Container sx={{ marginTop: 8 }}>
      <Box
        display="flex"
        justifyContent="space-between"
        alignItems="center"
        marginBottom={4}
      >
        <Typography variant="h4" gutterBottom>
          Панель управления
        </Typography>
        <Button
          variant="outlined"
          color="secondary"
          onClick={handleLogout}
        >
          Выйти
        </Button>
      </Box>

      <Typography variant="h6" gutterBottom>
        Добро пожаловать, {userStore.user?.username}!
      </Typography>

      <Grid container spacing={3}>
        {sections.map((section) => (
          <Grid item xs={12} sm={6} md={4} key={section.title}>
            <Card>
              <CardContent>
                <Typography variant="h6" gutterBottom>
                  {section.title}
                </Typography>
                <Typography variant="body2" color="text.secondary">
                  {section.description}
                </Typography>
              </CardContent>
              <CardActions>
                <Button
                  variant="contained"
                  component="a"
                  href={section.path}
                >
                  Перейти
                </Button>
              </CardActions>
            </Card>
          </Grid>
        ))}
      </Grid>
    </Container>
  );
});

export default DashboardPage;
