import axios from 'axios';

const API_URL = 'http://localhost:5000';

// Получение встреч
export const fetchBookings = async (username, role) => {
  const response = await axios.get(`${API_URL}/bookings`, {
    params: { username, role },
  });
  return response.data;
};

// Создание встречи
export const createBooking = async (booking) => {
  try {
    const response = await axios.post(`${API_URL}/bookings`, booking);
    return response.data;
  } catch (error) {
    console.error('Ошибка при создании встречи:', error.response.data);
    throw error;
  }
};

// Обновление встречи
export const updateBooking = async (id, booking) => {
  const response = await axios.put(`${API_URL}/bookings/${id}`, booking);
  return response.data;
};

// Удаление встречи
export const deleteBooking = async (id) => {
  const response = await axios.delete(`${API_URL}/bookings/${id}`);
  return response.data;
};
