import React from 'react';
import { observer } from 'mobx-react-lite';
import useStores from '../hooks/useStores';
import {
  Container,
  Typography,
  Button,
  Card,
  CardContent,
  CardActions,
  Grid,
} from '@mui/material';

const SchedulePage = observer(() => {
  const { scheduleStore } = useStores();

  const handleDelete = (id) => {
    scheduleStore.removeBooking(id);
  };

  return (
    <Container sx={{ marginTop: 8 }}>
      <Typography variant="h4" gutterBottom align="center">
        Список встреч
      </Typography>

      {scheduleStore.bookings.length === 0 ? (
        <Typography align="center" color="text.secondary">
          Встречи отсутствуют
        </Typography>
      ) : (
        <Grid container spacing={2}>
          {scheduleStore.bookings.map((item) => (
            <Grid item xs={12} md={6} lg={4} key={item.id}>
              <Card>
                <CardContent>
                  <Typography variant="h6">{item.title}</Typography>
                  <Typography>
                    Дата: {item.start.toLocaleDateString()} | Время:{' '}
                    {item.start.toLocaleTimeString()}
                  </Typography>
                </CardContent>
                <CardActions>
                  <Button
                    variant="outlined"
                    color="secondary"
                    onClick={() => handleDelete(item.id)}
                  >
                    Удалить
                  </Button>
                </CardActions>
              </Card>
            </Grid>
          ))}
        </Grid>
      )}
    </Container>
  );
});

export default SchedulePage;
