import React, { useState, useEffect } from 'react';
import { observer } from 'mobx-react-lite';
import useStores from '../hooks/useStores';
import NavBar from '../components/NavBar';
import { createBooking, deleteBooking, fetchBookings } from '../services/api';
import {
  Container,
  Typography,
  TextField,
  Button,
  Box,
  Paper,
  Grid,
  Card,
  CardContent,
  CardActions,
  Dialog,
  DialogActions,
  DialogContent,
  DialogContentText,
  DialogTitle,
} from '@mui/material';

// Форматирование даты
const formatDateTimeLocal = (date) => {
  return new Date(date).toISOString().slice(0, 16); // Убираем :00.000Z
};

const BookingPage = observer(() => {
  const { scheduleStore } = useStores();
  const [title, setTitle] = useState('');
  const [startDateTime, setStartDateTime] = useState('');
  const [endDateTime, setEndDateTime] = useState('');
  const [openDeleteDialog, setOpenDeleteDialog] = useState(false);
  const [selectedBooking, setSelectedBooking] = useState(null);

  useEffect(() => {
    const loadBookings = async () => {
      const data = await fetchBookings();
      scheduleStore.setBookings(data);
    };
    loadBookings();
  }, [scheduleStore]);

  // Создание встречи
  const handleCreate = async () => {
    if (!title || !startDateTime || !endDateTime) {
      alert('Заполните все поля!');
      return;
    }

    const start = formatDateTimeLocal(startDateTime);
    const end = formatDateTimeLocal(endDateTime);

    if (new Date(start) >= new Date(end)) {
      alert('Дата и время начала должны быть раньше даты и времени окончания!');
      return;
    }

    try {
      const newBooking = await createBooking({
        title,
        start,
        end,
      });

      scheduleStore.addBooking(newBooking);

      alert(`Встреча успешно создана до ${end}`);

      // Очистка полей
      setTitle('');
      setStartDateTime('');
      setEndDateTime('');
    } catch (error) {
      console.error('Ошибка при создании встречи:', error);
      alert('Ошибка при создании встречи!');
    }
  };

  // Открытие диалога удаления
  const handleOpenDeleteDialog = (booking) => {
    setSelectedBooking(booking);
    setOpenDeleteDialog(true);
  };

  // Закрытие диалога удаления
  const handleCloseDeleteDialog = () => {
    setOpenDeleteDialog(false);
    setSelectedBooking(null);
  };

  // Удаление встречи
  const handleDelete = async () => {
    if (selectedBooking) {
      try {
        await deleteBooking(selectedBooking.id);
        scheduleStore.removeBooking(selectedBooking.id);
        alert('Встреча успешно удалена!');
      } catch (error) {
        console.error('Ошибка при удалении встречи:', error);
        alert('Ошибка при удалении!');
      } finally {
        handleCloseDeleteDialog();
      }
    }
  };

  return (
    <>
      <NavBar />
      <Container
        sx={{
          display: 'flex',
          justifyContent: 'center',
          alignItems: 'center',
          minHeight: 'calc(100vh - 64px)',
          flexDirection: 'column',
        }}
      >
        <Paper
          elevation={3}
          sx={{
            padding: 4,
            width: '100%',
            maxWidth: 600,
            boxSizing: 'border-box',
            marginBottom: 4,
          }}
        >
          <Typography variant="h4" gutterBottom align="center">
            Создание встречи
          </Typography>

          <Box
            component="form"
            sx={{
              display: 'flex',
              flexDirection: 'column',
              gap: 2,
            }}
          >
            <Typography variant="h6">Название встречи</Typography>
            <TextField
              variant="outlined"
              value={title}
              onChange={(e) => setTitle(e.target.value)}
              required
            />

            <Typography variant="h6">Дата и время начала встречи</Typography>
            <TextField
              type="datetime-local"
              variant="outlined"
              value={startDateTime}
              onChange={(e) => setStartDateTime(e.target.value)}
              required
            />

            <Typography variant="h6">Дата и время окончания встречи</Typography>
            <TextField
              type="datetime-local"
              variant="outlined"
              value={endDateTime}
              onChange={(e) => setEndDateTime(e.target.value)}
              required
            />

            <Button
              variant="contained"
              color="primary"
              onClick={handleCreate}
              size="large"
            >
              Создать встречу
            </Button>
          </Box>
        </Paper>

        <Typography variant="h5" align="center" sx={{ marginBottom: 2 }}>
          Список встреч
        </Typography>

        <Grid container spacing={3}>
          {scheduleStore.bookings.length === 0 ? (
            <Typography align="center" color="text.secondary">
              Встречи отсутствуют
            </Typography>
          ) : (
            scheduleStore.bookings.map((booking) => (
              <Grid item xs={12} sm={6} md={4} key={booking.id}>
                <Card>
                  <CardContent>
                    <Typography variant="h6">{booking.title}</Typography>
                    <Typography>
                      <strong>Начало:</strong>{' '}
                      {new Date(booking.start).toLocaleString('ru-RU')}
                    </Typography>
                    <Typography>
                      <strong>Окончание:</strong>{' '}
                      {new Date(booking.end).toLocaleString('ru-RU')}
                    </Typography>
                  </CardContent>
                  <CardActions>
                    <Button
                      variant="outlined"
                      color="secondary"
                      onClick={() => handleOpenDeleteDialog(booking)}
                    >
                      Удалить
                    </Button>
                  </CardActions>
                </Card>
              </Grid>
            ))
          )}
        </Grid>

        {/* Диалоговое окно подтверждения удаления */}
        <Dialog
          open={openDeleteDialog}
          onClose={handleCloseDeleteDialog}
        >
          <DialogTitle>Подтверждение удаления</DialogTitle>
          <DialogContent>
            <DialogContentText>
              Вы уверены, что хотите удалить встречу "
              {selectedBooking?.title}"?
            </DialogContentText>
          </DialogContent>
          <DialogActions>
            <Button onClick={handleCloseDeleteDialog} color="primary">
              Отмена
            </Button>
            <Button onClick={handleDelete} color="secondary" autoFocus>
              Удалить
            </Button>
          </DialogActions>
        </Dialog>
      </Container>
    </>
  );
});

export default BookingPage;
