import React from 'react';
import { useQuery, useMutation, useQueryClient } from '@tanstack/react-query';
import { fetchSchedule, deleteBooking } from '../services/api';
import {
  Container,
  Typography,
  Button,
  Card,
  CardContent,
  CardActions,
  Grid,
} from '@mui/material';

const SchedulePage = () => {
  const queryClient = useQueryClient();

  const { data: schedule = [], isLoading, error } = useQuery({
    queryKey: ['schedule'],
    queryFn: fetchSchedule,
  });

  const deleteMutation = useMutation({
    mutationFn: deleteBooking,
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ['schedule'] });
    },
  });

  const handleDelete = (id) => {
    deleteMutation.mutate(id);
  };

  if (isLoading) return <Typography>Загрузка расписания...</Typography>;
  if (error) return <Typography>Ошибка: {error.message}</Typography>;

  return (
    <Container>
      <Typography variant="h4" gutterBottom>
        Расписание встреч
      </Typography>

      <Grid container spacing={2}>
        {schedule.map((item) => (
          <Grid item xs={12} md={6} lg={4} key={item.id}>
            <Card>
              <CardContent>
                <Typography variant="h6">{item.title}</Typography>
                <Typography>
                  Дата: {item.date} | Время: {item.time}
                </Typography>
              </CardContent>
              <CardActions>
                <Button
                  variant="outlined"
                  color="secondary"
                  onClick={() => handleDelete(item.id)}
                >
                  Удалить
                </Button>
              </CardActions>
            </Card>
          </Grid>
        ))}
      </Grid>
    </Container>
  );
};

export default SchedulePage;
