import React from 'react';
import { observer } from 'mobx-react-lite';
import useStores from '../hooks/useStores';
import {
  Container,
  Typography,
  Grid,
  Card,
  CardContent,
  CardActions,
  Button,
} from '@mui/material';
import { Link } from 'react-router-dom';

const DashboardPage = observer(() => {
  const { userStore } = useStores();

  const sections = [
    { title: 'Расписание', path: '/schedule', description: 'Просмотр и управление встречами' },
    { title: 'Создать встречу', path: '/bookings/new', description: 'Создание новой встречи' },
    { title: 'Календарь', path: '/calendar', description: 'Просмотр встреч в календаре' },
  ]; 

  return (
    <Container sx={{ marginTop: 8 }}>
      <Typography variant="h4" gutterBottom align="center">
        Панель управления
      </Typography>

      <Typography variant="h6" gutterBottom align="center">
        Добро пожаловать, {userStore.user?.username}!
      </Typography>

      <Grid container spacing={3}>
        {sections.map((section) => (
          <Grid item xs={12} sm={6} md={4} key={section.title}>
            <Card>
              <CardContent>
                <Typography variant="h6" gutterBottom>
                  {section.title}
                </Typography>
                <Typography variant="body2" color="text.secondary">
                  {section.description}
                </Typography>
              </CardContent>
              <CardActions>
                <Button
                  variant="contained"
                  component={Link}
                  to={section.path}
                >
                  Перейти
                </Button>
              </CardActions>
            </Card>
          </Grid>
        ))}
      </Grid>
    </Container>
  );
});

export default DashboardPage;
