import React, { useEffect } from 'react';
import NavBar from '../components/NavBar';
import { observer } from 'mobx-react-lite';
import useStores from '../hooks/useStores';
import { fetchBookings } from '../services/api';
import {
  Container,
  Typography,
  Grid,
  Card,
  CardContent,
  CardActions,
  Button,
} from '@mui/material';

const SchedulePage = observer(() => {
  const { scheduleStore } = useStores();

  useEffect(() => {
    const loadBookings = async () => {
      const data = await fetchBookings();
      scheduleStore.setBookings(data);
    };
    loadBookings();
  }, [scheduleStore]);

  return (
    <>
      <NavBar />
      <Container>
        <Typography variant="h4" gutterBottom align="center">
          Список встреч
        </Typography>

        {scheduleStore.bookings.length === 0 ? (
          <Typography align="center" color="text.secondary">
            Встречи отсутствуют
          </Typography>
        ) : (
          <Grid container spacing={3}>
            {scheduleStore.bookings.map((booking) => (
              <Grid item xs={12} md={6} lg={4} key={booking.id}>
                <Card>
                  <CardContent>
                    <Typography variant="h6">{booking.title}</Typography>
                    <Typography>
                      <strong>Начало:</strong>{' '}
                      {new Date(booking.start).toLocaleString('ru-RU')}
                    </Typography>
                    <Typography>
                      <strong>Окончание:</strong>{' '}
                      {new Date(booking.end).toLocaleString('ru-RU')}
                    </Typography>
                  </CardContent>
                  <CardActions>
                    <Button
                      variant="outlined"
                      color="secondary"
                      onClick={() => scheduleStore.removeBooking(booking.id)}
                    >
                      Удалить
                    </Button>
                  </CardActions>
                </Card>
              </Grid>
            ))}
          </Grid>
        )}
      </Container>
    </>
  );
});

export default SchedulePage;
