import { makeAutoObservable } from 'mobx';

class UserStore {
  user = null;

  constructor() {
    makeAutoObservable(this);
  }

  login(username) {
    this.user = { username }; // Установка имени пользователя
  }

  logout() {
    this.user = null; // Очистка состояния пользователя
  }
}

const userStore = new UserStore();
export default userStore;
