import React, { useState, useEffect } from 'react';
import { Calendar, dateFnsLocalizer } from 'react-big-calendar';
import { format, parse, startOfWeek, getDay } from 'date-fns';
import { enUS, ru } from 'date-fns/locale';
import 'react-big-calendar/lib/css/react-big-calendar.css';
import { observer } from 'mobx-react-lite';
import useStores from '../hooks/useStores';
import {
  Container,
  Typography,
  Button,
  Dialog,
  DialogTitle,
  DialogContent,
  TextField,
  DialogActions,
} from '@mui/material';

// Конфигурация локализации
const locales = { enUS, ru };
const localizer = dateFnsLocalizer({
  format,
  parse,
  startOfWeek: () => startOfWeek(new Date(), { weekStartsOn: 1 }),
  getDay,
  locales,
});

const CalendarPage = observer(() => {
  const { scheduleStore } = useStores();
  const [selectedEvent, setSelectedEvent] = useState(null);
  const [newEvent, setNewEvent] = useState({
    title: '',
    start: new Date(),
    end: new Date(),
  });
  const [open, setOpen] = useState(false);

  useEffect(() => {
    // Здесь можно подгрузить данные с сервера
  }, []);

  const handleSelectSlot = ({ start, end }) => {
    setNewEvent({ ...newEvent, start, end });
    setOpen(true);
  };

  const handleSelectEvent = (event) => {
    setSelectedEvent(event);
  };

  const handleClose = () => {
    setOpen(false);
    setSelectedEvent(null);
  };

  const handleSave = () => {
    scheduleStore.addBooking(newEvent);
    setOpen(false);
  };

  return (
    <Container sx={{ marginTop: 8 }}>
      <Typography variant="h4" gutterBottom align="center">
        Календарь встреч
      </Typography>

      <Calendar
        localizer={localizer}
        events={scheduleStore.bookings}
        startAccessor="start"
        endAccessor="end"
        style={{ height: 600 }}
        selectable
        onSelectEvent={handleSelectEvent}
        onSelectSlot={handleSelectSlot}
      />

      <Dialog open={open} onClose={handleClose}>
        <DialogTitle>Добавить встречу</DialogTitle>
        <DialogContent>
          <TextField
            label="Название встречи"
            fullWidth
            margin="normal"
            value={newEvent.title}
            onChange={(e) =>
              setNewEvent({ ...newEvent, title: e.target.value })
            }
          />
          <TextField
            label="Дата начала"
            type="datetime-local"
            fullWidth
            margin="normal"
            value={format(newEvent.start, "yyyy-MM-dd'T'HH:mm")}
            onChange={(e) =>
              setNewEvent({
                ...newEvent,
                start: new Date(e.target.value),
              })
            }
          />
          <TextField
            label="Дата окончания"
            type="datetime-local"
            fullWidth
            margin="normal"
            value={format(newEvent.end, "yyyy-MM-dd'T'HH:mm")}
            onChange={(e) =>
              setNewEvent({
                ...newEvent,
                end: new Date(e.target.value),
              })
            }
          />
        </DialogContent>
        <DialogActions>
          <Button onClick={handleClose} color="secondary">
            Отмена
          </Button>
          <Button onClick={handleSave} variant="contained" color="primary">
            Сохранить
          </Button>
        </DialogActions>
      </Dialog>
    </Container>
  );
});

export default CalendarPage;
