import React from 'react';
import { AppBar, Toolbar, Typography, Button, Box } from '@mui/material';
import { Link, useNavigate } from 'react-router-dom';
import useStores from '../hooks/useStores';

const NavBar = () => {
  const navigate = useNavigate();
  const { userStore } = useStores();

  const handleLogout = () => {
    userStore.logout();
    navigate('/');
  };

  return (
    <AppBar position="static" sx={{ background: '#00796b', padding: 2 }}>
      <Toolbar sx={{ display: 'flex', justifyContent: 'space-between' }}>
        <Typography variant="h6">Панель управления</Typography>

        <Box sx={{ display: 'flex', gap: 2 }}>
          <Button color="inherit" component={Link} to="/dashboard">
            Главная
          </Button>

          {userStore.user?.role === 'admin' && (
            <Button color="inherit" component={Link} to="/bookings/new">
              Создать встречу
            </Button>
          )}

          <Button color="inherit" component={Link} to="/schedule">
            Расписание
          </Button>

          <Button color="inherit" component={Link} to="/calendar">
            Календарь
          </Button>

          <Button color="inherit" onClick={handleLogout}>
            Выйти
          </Button>
        </Box>
      </Toolbar>
    </AppBar>
  );
};

export default NavBar;
